function A = getA()
	A = [[0, 0, 1, 0];
		[0, 0, 0, 1];
		[(L1*g*l2^2*m2^2*cos(q1) - 2*L1*l2^3*m2^2*q2_dot^2*cos(q1 - q2) + 2*J2*L1*g*m2*cos(q1) + L1*g*l2^2*m2^2*cos(q1 - 2*q2) + 2*J2*g*l1*m1*cos(q1) - 2*L1^2*l2^2*m2^2*q1_dot^2*cos(2*q1 - 2*q2) - 2*L1*l2*m2*q2_dot*r2*sin(q1 - q2) - 2*J2*L1*l2*m2*q2_dot^2*cos(q1 - q2) + 2*g*l1*l2^2*m1*m2*cos(q1))/p, (2*L1*l2*m2*(q2_dot*r2*sin(q1 - q2) + J2*q2_dot^2*cos(q1 - q2) + l2^2*m2*q2_dot^2*cos(q1 - q2) - g*l2*m2*cos(q1 - 2*q2) + L1*l2*m2*q1_dot^2*cos(2*q1 - 2*q2)))/p, -(2*J2*r1 + 2*l2^2*m2*r1 + 2*L1^2*l2^2*m2^2*q1_dot*sin(2*q1 - 2*q2))/p, -(2*L1*l2*m2*(2*J2*q2_dot*sin(q1 - q2) - r2*cos(q1 - q2) + 2*l2^2*m2*q2_dot*sin(q1 - q2)))/p];
		[(2*L1*l2*m2*(u*sin(q1 - q2) - q1_dot*r1*sin(q1 - q2) + J1*q1_dot^2*cos(q1 - q2) - g*l1*m1*cos(2*q1 - q2) + L1^2*m2*q1_dot^2*cos(q1 - q2) + l1^2*m1*q1_dot^2*cos(q1 - q2) - L1*g*m2*cos(2*q1 - q2) + L1*l2*m2*q2_dot^2*cos(2*q1 - 2*q2)))/p, -(l2*m2*(2*L1*u*sin(q1 - q2) - 2*J1*g*cos(q2) - L1^2*g*m2*cos(2*q1 - q2) + 2*L1^3*m2*q1_dot^2*cos(q1 - q2) - 2*L1*q1_dot*r1*sin(q1 - q2) - L1^2*g*m2*cos(q2) + 2*J1*L1*q1_dot^2*cos(q1 - q2) - 2*g*l1^2*m1*cos(q2) + 2*L1^2*l2*m2*q2_dot^2*cos(2*q1 - 2*q2) - L1*g*l1*m1*cos(2*q1 - q2) + 2*L1*l1^2*m1*q1_dot^2*cos(q1 - q2) + L1*g*l1*m1*cos(q2)))/p, (2*L1*l2*m2*(r1*cos(q1 - q2) + 2*J1*q1_dot*sin(q1 - q2) + 2*l1^2*m1*q1_dot*sin(q1 - q2) + 2*L1^2*m2*q1_dot*sin(q1 - q2)))/p, -(2*J1*r2 + 2*l1^2*m1*r2 + 2*L1^2*m2*r2 - 2*L1^2*l2^2*m2^2*q2_dot*sin(2*q1 - 2*q2))/p]];
end
